import React from "react";
import CharacterCard from "./components/CharacterCard";
import Wrapper from "./components/Wrapper";
import characters from "./characters.json";
import "./App.css";

class App extends React.Component {
  state = {
    characters,
    charactersChosen: [],
    score: 0,
    highScore: 0,
    status: ""
  };

  handleInputChange = event => {
    event.preventDefault();
    let { name, value } = event.target;
    this.setState({
      [name]: value
    });
  };

  chooseCharacter = id => {


    console.log("this.state.characters is ");
    console.log(this.state.characters);

    if (!this.state.score) {
      this.setState({
        status: ""
      })
    }

    else if (this.state.score === 12) {
      this.setState({
        status: "",
        charactersChosen: [],
        score: 0
      })

    }
    const shuffler = array => {
      array.sort(() => Math.random() - 0.5);
    }


    if (!this.state.charactersChosen.includes(id)) {
      if (this.state.score >= 11) {
        this.setState({
          status: "You won! Congratulations!",
          highScore: 12,
          score: 0,
          charactersChosen: [],
        })
        console.log("You won!");

      }
      else {
        this.setState({
          charactersChosen: [...this.state.charactersChosen, id],
          score: this.state.score + 1,
          characters: shuffler(characters)
        })
      }
    }
    else {
      let high;
      if (this.state.score > this.state.highScore) {
        high = this.state.score;
      }
      else {
        high = this.state.highScore;
      }
      console.log("You lose!")

      this.setState({
        charactersChosen: [],
        highScore: high,
        score: 0,
        status: "You lost! Whoops. Refresh or click any picture to play again!"
      })
    }





  }

  render() {
    return (
      <Wrapper>
        {console.log("this.state is")}
        {console.log(this.state)}


        <div className="jumbotron">
          <h1 className="title">Star Wars Clicky Game!</h1>
        </div>

        <div className="container">
          <div className="row stats">
            <div className="col-8">
            </div>
            <div className="col-2"> Score: {this.state.score}</div>
            <div className="col-2">
              High Score: {this.state.highScore}
            </div>
          </div>
          <div className="row">
            <div className="col-8">Click each character once. If you click all 12 only once, you win!</div>
            <div className="col-4"></div>
          </div>
          <h5 className="text-center mt-3">
            {this.state.status}

          </h5>

          <div className="row">
            {this.state.characters.map(item =>
              <CharacterCard
                name={item.name}
                image={item.image}
                id={item.id}
                onChange={this.handleInputChange}
                chooseCharacter={this.chooseCharacter}

              // removeFriend={this.removeFriend}
              />
              )}}

          </div>
        </div>





      </Wrapper >
    );
  }
}

export default App;
