    //     charactersChosen: this.state.charactersChosen.push(id),
    //   })
    // }
  }

  render() {
    return (
      <Wrapper>
        {console.log("this.state is")}
        {console.log(this.state)}
        <div class="container">

        <div className="jumbotron">
          <h1 className="title">Star Wars Clicky Game!</h1>
        </div>
        <div className="row stats">
          <div className="cm-8">Click each character once. If you click all 12 only once, you win!</div> 
          </div>
          <div className="row">
          <div className="cm-8"> </div><div className="cm-4"> Score: {this.state.score}</div>
          </div>
        <div className="row">
        {this.state.characters.map(item =>
          <CharacterCard
            name={item.name}
            image={item.image}
            id={item.id}
            chooseCharacter={this.chooseCharacter}

          // removeFriend={this.removeFriend}
          />)}
          </div>

          </div>


      </Wrapper>
    );
  }
}

export default App;
